import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isAboutDropdownOpen, setIsAboutDropdownOpen] = useState(false);

  const offerings = [
    { name: 'Energy Healing', id: 'energy-healing' },
    { name: 'Life Coaching', id: 'life-coaching' },
    { name: 'Spiritual Guidance', id: 'spiritual-guidance' },
    { name: 'Meditation Sessions', id: 'meditation' },
    { name: 'Chakra Balancing', id: 'chakra-balancing' }
  ];

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleAboutDropdown = () => {
    setIsAboutDropdownOpen(!isAboutDropdownOpen);
  };

  const scrollToOffering = (offeringId) => {
    setIsAboutDropdownOpen(false);
    setIsMenuOpen(false);
    // Navigate to about page and scroll to section
    window.location.href = `/about#${offeringId}`;
  };

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <Link to="/" className="logo">
            <h1>Kalyāṇamitta</h1>
          </Link>

          <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
            <ul className="nav-list">
              <li className="nav-item">
                <Link to="/" className="nav-link">Home</Link>
              </li>
              <li className="nav-item dropdown">
                <button 
                  className="nav-link dropdown-toggle"
                  onClick={toggleAboutDropdown}
                  onMouseEnter={() => setIsAboutDropdownOpen(true)}
                  onMouseLeave={() => setIsAboutDropdownOpen(false)}
                >
                  About
                </button>
                {isAboutDropdownOpen && (
                  <ul className="dropdown-menu">
                    {offerings.map((offering) => (
                      <li key={offering.id}>
                        <button 
                          className="dropdown-item"
                          onClick={() => scrollToOffering(offering.id)}
                        >
                          {offering.name}
                        </button>
                      </li>
                    ))}
                  </ul>
                )}
              </li>
              <li className="nav-item">
                <Link to="/offerings" className="nav-link">Offerings</Link>
              </li>
              <li className="nav-item">
                <Link to="/rates" className="nav-link">Rates</Link>
              </li>
              <li className="nav-item">
                <Link to="/contact" className="nav-link">Contact</Link>
              </li>
            </ul>
          </nav>

          <button 
            className="menu-toggle"
            onClick={toggleMenu}
            aria-label="Toggle menu"
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
