import React, { useEffect } from 'react';
import './CalendlyEmbed.css';

const CalendlyEmbed = () => {
  useEffect(() => {
    // Load Calendly script
    const script = document.createElement('script');
    script.src = 'https://assets.calendly.com/assets/external/widget.js';
    script.async = true;
    document.head.appendChild(script);

    return () => {
      // Cleanup script on unmount
      const existingScript = document.querySelector('script[src="https://assets.calendly.com/assets/external/widget.js"]');
      if (existingScript) {
        document.head.removeChild(existingScript);
      }
    };
  }, []);

  const sessionTypes = [
    {
      id: 'energy-healing',
      name: 'Energy Healing Session',
      duration: '60-90 minutes',
      description: 'Restore balance and vitality through gentle energy work',
      url: 'https://calendly.com/kalyanamitta/energy-healing'
    },
    {
      id: 'life-coaching',
      name: 'Life Coaching Session',
      duration: '60 minutes',
      description: 'Transform your life path with personalized guidance',
      url: 'https://calendly.com/kalyanamitta/life-coaching'
    },
    {
      id: 'spiritual-guidance',
      name: 'Spiritual Guidance Session',
      duration: '75 minutes',
      description: 'Connect with your higher self and spiritual path',
      url: 'https://calendly.com/kalyanamitta/spiritual-guidance'
    },
    {
      id: 'meditation',
      name: 'Meditation Session',
      duration: '45-60 minutes',
      description: 'Find inner peace through guided meditation',
      url: 'https://calendly.com/kalyanamitta/meditation'
    },
    {
      id: 'chakra-balancing',
      name: 'Chakra Balancing Session',
      duration: '90 minutes',
      description: 'Align your energy centers for optimal well-being',
      url: 'https://calendly.com/kalyanamitta/chakra-balancing'
    },
    {
      id: 'intuitive-reading',
      name: 'Intuitive Reading Session',
      duration: '60 minutes',
      description: 'Gain clarity and insight through intuitive guidance',
      url: 'https://calendly.com/kalyanamitta/intuitive-reading'
    }
  ];

  const [selectedSession, setSelectedSession] = React.useState(sessionTypes[0]);

  const handleSessionChange = (session) => {
    setSelectedSession(session);
  };

  return (
    <div className="calendly-embed-container">
      <div className="calendly-header">
        <h2>Book Your Session</h2>
        <p>Choose the type of session you'd like to book and select your preferred time.</p>
      </div>

      <div className="calendly-content">
        <div className="session-selector">
          <h3>Select Session Type</h3>
          <div className="session-options">
            {sessionTypes.map((session) => (
              <div
                key={session.id}
                className={`session-option ${selectedSession.id === session.id ? 'selected' : ''}`}
                onClick={() => handleSessionChange(session)}
              >
                <div className="session-info">
                  <h4>{session.name}</h4>
                  <p className="session-duration">{session.duration}</p>
                  <p className="session-description">{session.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="calendly-widget">
          <div className="calendly-inline-widget" 
               data-url={selectedSession.url}
               style={{ minWidth: '320px', height: '700px' }}>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalendlyEmbed;
